package org.orgst;

public class App {
    String[] yes_ins = {"Yes","yes","Y","y"};
   
    public static void main(String[] args) {
        Methods.head();
        while (true){
        String Cmd = Methods.input();
        String[] words = Cmd.split("\\s+");
        switch (words[0]) {
            case "run": Methods.run();
            case "help":
                Methods.help(words);
                break;
            case "sauce":
                Methods.sauce();
                break;
            case "clear":
                Methods.clear();
                break;
            case "hist":
                Methods.hist();
                break;
            case "orgid":
                Methods.orgid();
                break;
            case "pride":
                Methods.flag();
                break;
            case "esc":
                Methods.clear();
                System.exit(0);
                break;
            case "git":
                System.out.println(org.orgst.Variables.AppData.AppData.get("GitHub"));
            default:
                break;
        }
    }
    }
}
